const { Client, logger, VariableService } = require("./index.js");

// configuration for the Client:
//  - 'baseUrl': url to the Workflow Engine
//  - 'logger': utility to automatically log important events
const config = { baseUrl: "http://localhost:8080/engine-rest", use: logger };

// create a Client instance with custom configuration
const client = new Client(config);

// create a handler for the task
const handler = async ({ task, taskService }) => {
  // get task variable 'defaultScore'
  // const startDate = task.variables.get('StartDate');
  //

	const variables = new VariableService();

  variables.set('manipulatedDate', new Date("2018-01-23T14:42:45.435+0200"));

  variables.setTyped("manipulatedDate", {
    value: "2018-01-23T14:42:45.435+0200",
    type: "Date",
    valueInfo: {
      objectTypeName: "Date",
      serializationDataFormat: "application/json"
    }
  });

  // console.log(task.variables.getTyped('manipulatedDate'));

  // complete the task
  try {
    await taskService.complete(task, variables);
    console.log("I completed my task successfully!!");
  } catch (e) {
    console.error(`Failed completing my task, ${e}`);
  }
};

// susbscribe to the topic 'creditScoreChecker' & provide the created handler
client.subscribe("foo", handler);
